/* ==UserStyle==
@name           google.pt - 6/9/2019, 1:10:45 AM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me

==/UserStyle== */

/*
     * Name:    DarkSearch for Google
     * Author:  Nass O
     * Version: v2.1.8+
     */


    :root
    {
        --main-bg-col: #101010;
        --card-bg-col: #151515;
        --pop-card-bg-col: #1A1A1A;
        --search-bar-bg: #222;
        --border-main-col: #333;
        --image-label-bg: rgba(127, 127, 127, .05);

        --link-blue-col: #5070EC;
        --link-blue-title-col: #9BAED2;
        --link-blue-title-strong-col: #36C;
        --link-visited-col: #B8A7C3;

        --small-text-col0: #777;
        --small-text-col1: #A3A3A3;
        --small-text-col2: #BDBDBD;
        --small-text-col3: rgba(255, 255, 255, 0.87);

        --label-text-col0: #666;
        --label-text-col1: rgba(255, 255, 255, 0.51);
        --label-text-col2: #c5c5c5;

        --highlight-pop-selection-bg: rgba(127, 127, 127, .1);

        --base-box-shadow: 3px 3px 3px rgba(0, 0, 0, .2);
    }

    /* Background */
    html, body, #cnt, .rg_l, #botabar {
        background: var(--main-bg-col) !important;
        color: #c5c5c5;
    }
    /* Inverting colors */
    .qbh, .s2tb .google-logo/*, .gb_Vb*/, #wob_gsp, ._kac, #qbi, .ab_icon, .gsok_a, ._Fnc, ._Jxg, .stt-uf, ._dVi, .kcb.checked::after, ._gW, .z3Trqc {
        -webkit-filter: invert(1) grayscale(1) !important;
        -moz-filter: invert(1) grayscale(1) !important;
        filter: invert(1) grayscale(1) !important;
    }
    ._Zgf {
        -webkit-filter: invert(.6) grayscale(1) !important;
        -moz-filter: invert(.6) grayscale(1) !important;
        filter: invert(.6) grayscale(1) !important;
    }
    .gsri_a, .sbico {
        -webkit-filter: grayscale(.6) !important;
        -moz-filter: grayscale(.6) !important;
        filter: grayscale(.6) !important;
    }
    ._n0b, ._MNk._wtf svg {
        -webkit-filter: invert(.9) !important;
        -moz-filter: invert(.9) !important;
        filter: invert(.9) !important;
    }
    #qbx {
        -webkit-filter: invert(.3) !important;
        -moz-filter: invert(.3) !important;
        filter: invert(.3) !important;
    }
    /* Images opacity */
