/* ==UserStyle==
@name           DarkSearch - Nass O (fork)
@namespace      USO Archive
@author         maujogador
@description    `https://userstyles.org/styles/118959/darksearch-for-googleUpdated version of that ^Ignore the license above, this is GPLv3 licensedOptimized for stylus: https://chrome.google.com/webstore/detail/stylus/clngdbkpkpeebahjckkjfobafhncgmne/related?hl=en`
@version        20201201.22.52
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
/* ==UserStyle==
@name           google.pt - 6/9/2019, 1:10:45 AM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me

==/UserStyle== */

@-moz-document
	regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?."),
	regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"),
	regexp("https?://encrypted.google.(com|([a-z]{2}))(.[a-z]{2})?\/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"),
	regexp("https?://encrypted.google.(com|([a-z]{2}))(.[a-z]{2})?."),
	regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?\/_\/chrome\/newtab\?.*")
{

	/*
     * Name:    DarkSearch for Google
     * Author:  Nass O
     * Version: v2.1.8+
     */


    :root
    {
        --main-bg-col: #101010;
        --card-bg-col: #151515;
        --pop-card-bg-col: #1A1A1A;
        --search-bar-bg: #222;
        --border-main-col: #333;
        --image-label-bg: rgba(127, 127, 127, .05);

        --link-blue-col: #5070EC;
        --link-blue-title-col: #9BAED2;
        --link-blue-title-strong-col: #36C;
        --link-visited-col: #B8A7C3;

        --small-text-col0: #777;
        --small-text-col1: #A3A3A3;
        --small-text-col2: #BDBDBD;
        --small-text-col3: rgba(255, 255, 255, 0.87);

        --label-text-col0: #666;
        --label-text-col1: rgba(255, 255, 255, 0.51);
        --label-text-col2: #c5c5c5;

        --highlight-pop-selection-bg: rgba(127, 127, 127, .1);

        --base-box-shadow: 3px 3px 3px rgba(0, 0, 0, .2);
    }

    /* Background */
    html, body, #cnt, .rg_l, #botabar {
        background: var(--main-bg-col) !important;
        color: #c5c5c5;
    }
    /* Inverting colors */
    .qbh, .s2tb .google-logo/*, .gb_Vb*/, #wob_gsp, ._kac, #qbi, .ab_icon, .gsok_a, ._Fnc, ._Jxg, .stt-uf, ._dVi, .kcb.checked::after, ._gW, .z3Trqc {
        -webkit-filter: invert(1) grayscale(1) !important;
        -moz-filter: invert(1) grayscale(1) !important;
        filter: invert(1) grayscale(1) !important;
