  .content .collapse-toggle:hover,
  h1 .srclink:hover,
  h3.impl .srclink:hover,
  h4.method .srclink:hover,
  h1 #toggle-all-docs:hover,
  h3 > a.collapse-toggle:hover,
  h4 > a.collapse-toggle:hover,
  h1 #toggle-all-docs:focus,
