  }
  @media (max-width: 700px) {
    nav.sub,
    .content {
      margin-left: 0;
    }
    .sidebar {
      min-height: 50px;
      position: relative;
      width: 100%;
      border-bottom: unset;
      margin: 0;
      padding: 0;
    }
    .sidebar.mobile {
