    }
    .content h1 > .out-of-band,
    ul.item-list > li > .out-of-band,
    h3.impl > .out-of-band,
    h4.method > .out-of-band {
      float: right;
      position: unset;
    }
    .content .in-band {
      width: unset;
    }
  }
  @media (max-width: 416px) {
    nav.sub {
      float: right;
      width: 100%;
    }
  }
  kbd {
    color: #000;
    background-color: #fafbfc;
    border-color: #888;
    border-bottom-color: #888;
    box-shadow-color: #c6cbd1;
  }
}

